    homeContent: {
        position: 'relative',
        boxSizing: 'border-box',
        display: 'block',
        paddingTop: 50,
    },
    img:{
        width:'100%',
        height:300
    }
});


ProductDetail.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(ProductDetail);
